%dw 2.0 output application/json fun getUrgency(urgency) = urgency match { case "1" -> "High" case "2" -> "Medium" case "3" -> "Low" else -> null }
fun getState(state) = state match { case "1" -> "New" case "2" -> "In Progress" case "3" -> "On Hold" case "6" -> "Resolved" case "7" -> "Closed" case "8" -> "Canceled" else -> null }
fun getImpact(impact) = impact match { case "1" -> "High" case "2" -> "Medium" case "3" -> "Low" else -> null }
fun getPriority(priority) = priority match { case "1" -> "Critical" case "2" -> "High" case "3" -> "Moderate" case "4" -> "Low" case "5" -> "Planning" else -> null } --- payload mapObject ( { "incident": { "incident_id": $.incident_id, "sys_id": $.sys_id, "description": $.description, "state": getState($.state), "urgency": getUrgency($.urgency), "impact": getImpact($.impact), "priority": getPriority($.priority), "comments": $.comments }
} )

%dw 2.0
output application/xml
---
{
  insert: {
    short_description: payload.description,
    comments: payload.comments,
    state: payload.state match {
        case str: "New" -> {"state": 1}
        case str: "In Progress" -> {"state": 2}
        case str: "On Hold" -> {"state": 3}
        case str: "Resolved" -> {"state": 4}
        case str: "Closed" -> {"state": 5}
        case str: "Canceled" -> {"state": 6}
    },
    urgency: payload.urgency match {
    	case str: "High" -> {"urgency": 1}
    	case str: "Medium" -> {"urgency": 2}
    	case str: "Low" -> {"urgency": 3}
    },
